to'  : jQuery('#country_id').val(),                       'impresion' : jQuery('#country_id2').val(),                       'unidad' : jQuery('#country_id3').val(),                       'cantidad' : jQuery('#cantidadAND').val(),                       'comentario' : jQuery('#comentarioAND').val()                   },                   success:function(){                       alert('Cotizacion enviada');                   }                });            }        </script>    </body></html>