                         //get the first list item and put it after the last list item (that's how the infinite effects is made) '                $('#carousel_ul li:last').after($('#carousel_ul li:first'));                                 //and get the left indent to the default -210px                $('#carousel_ul').css({'left' : '-185px'});            }); 						}			        });                //when user clicks the image for sliding left        $('#left_scroll img').click(function(){						var item_num = $('#carousel_ul li').size();						if(item_num>5){                        var item_width = $('#carousel_ul li').outerWidth() + 5;                        /* same as for sliding right except that it's current left indent + the item width (for the sliding right it's - item_width) */            var left_indent = parseInt($('#carousel_ul').css('left')) + item_width;                        $('#carousel_ul:not(:animated)').animate({'left' : left_indent},500,function(){                            /* when sliding to left we are moving the last item before the first list item */                        $('#carousel_ul li:first').before($('#carousel_ul li:last'));                         /* and again, when we make that change we are setting the left indent of our unordered list to the default -210px */            $('#carousel_ul').css({'left' : '-185px'});            });						}                                });  });</script><link href="css/stylos_mbabreton.css" rel="stylesheet" type="text/css" /><link href="fuentes/stylesheet.css" rel="stylesheet" type="text/css" />  <link rel="stylesheet" type="text/css" href="css/reset.css" />  <link rel="stylesheet" type="text/css" href="css/global.css" />  <link rel="stylesheet" type="text/css" href="css/sexyslider.css" />  <script type="text/javascript" src="highslide/highslide.js"></script><script type="text/javascript" src="highslide/highslide-with-html.js"></script><link rel="styleshee