INSERT INTO portfolio VALUES("2","Resposabilidad Social Corporativa","<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\"><font face=\"Arial\" size=\"2\">Diseñamos políticas de Responsabilidad Social Corporativa y sistemas de gestión, en el marco de las buenas prácticas y los estándares internacionales.&nbsp;</font></span></p>","2.JPG","21.png","2");
INSERT INTO portfolio VALUES("3","Riesgos socio - políticos y gestión de entorno","<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\"><font size=\"2\" face=\"Arial\">Analizamos los riesgos e impactos socio-políticos del entorno asociados al desarrollo de las actividades empresariales y diseñamos herramientas de gestión para su manejo.</font></span></p>","3.jpg","31.png","1");
INSERT INTO portfolio VALUES("5","Comunicación estratégica","<p class=\"MsoNormal\" style=\"text-align: justify;\"><font face=\"Arial\" size=\"2\">Diseñamos e implementamos estrategias de comunicación acordes con el plan de negocio, que permitan fortalecer la reputación interna y externa de la organización.</font></p>","5.jpg","51.png","5");
INSERT INTO portfolio VALUES("6","Regulación y gestión socio - ambiental","<div style=\"text-align: justify;\"><font size=\"2\" face=\"Arial\"><span style=\"line-height: 14px;\">Analizamos riesgos socio-ambientales y diseñamos estrategias de prevención y manejo. Asesoramos el diseño e implementación de proyectos de auto-gestión.</span></font></div>","6.jpg","61.png","6");
INSERT INTO portfolio VALUES("13","Derechos Humanos","<span style=\"color: rgb(102, 102, 102); font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: justify;\">Asesoramos en la construcción de políticas, planes y programas en Derechos Humanos. En la identificación y manejo de riesgos empresariales en DDHH. Así como en la generación de capacidades (talleres) y elaboración de protocolos corporativos.</span>","13.jpg","131.png","4");
INSERT INTO service VALUES("19","Reportes de sostenibilidad","","<div>Uno de los grandes retos de las organizaciones de hoy, es desarrollar sistemas de reporte para dar cuenta de su desempeño socio-ambiental en el área de influencia de sus actividades. Signum Consulting elabora Reportes de Sostenibilidad debidamente auditados y comunicados a grupos de interés internos y externos.</div><div><br></div>","19.jpg","5");
INSERT INTO service VALUES("23","Análisis de riesgos","","<p class=\"MsoNormal\" style=\"text-align: justify;\"><font size=\"2\" face=\"Arial\">Identificamos y\nanalizamos los riesgos en seguridad que pueden incidir negativamente en el\nnormal desarrollo de las operaciones. Diseñamos estrategias para prevenir y\natender dichos riesgos.</font><font size=\"3\" style=\"font-family: Arial, sans-serif;\"><o:p></o:p></font></p>","23.jpg","3");
INSERT INTO service VALUES("27","Políticas, programas y sistemas de gestión","","<div style=\"text-align: justify;\">Diseñamos políticas y programas de Responsabilidad Social Corporativa, así como sistemas de gestión que se adaptan a las características de las organizaciones. Buscamos factores diferenciadores e innovadores para que las empresas desarrollen liderazgo, aprovechando sus fortalezas e identificando las oportunidades que brinda el entorno. Utilizamos metodologías que facilitan el desarrollo efectivo de la política de RSC en la cadena de valor de la organización.</div><div><br></div>","27.JPG","2");
INSERT INTO service VALUES("29","Construcción de capacidades","","<div style=\"text-align: justify;\">Realizamos talleres (con la respectiva evaluación) para construir capacidades en RSC, identificando buenas prácticas y lecciones aprendidas. Nuestra metodología abarca la sensibilización y generación de capacidades en proveedores y contratistas. Generamos protocolos o guías empresariales para promover la aplicación de buenas prácticas en RSC y diseñamos mecanismos de aseguramiento para garantizar coherencia y sostenibilidad en el desempeño socio-ambiental en las organizaciones.</div><div><br></div>","29.jpg","2");
INSERT INTO service VALUES("30","Estándares e indicadores","","<div style=\"text-align: justify;\">Asesoramos a las organizaciones en la adopción de estándares nacionales e internacionales en Responsabilidad Social Corporativa. Construimos indicadores de desempeño e impacto para el seguimiento y evaluación de la gestión social de la empresa y para la elaboración de sistemas de reporte debidamente sustentados y comunicados.</div><div><br></div>","30.jpg","2");
INSERT INTO service VALUES("36","Relacionamiento con stakeholders","","<div style=\"text-align: justify;\">Diseñamos e implementamos estrategias para generar relaciones efectivas con los grupos de interés (Gobierno nacional y local, comunidades, medios de comunicación, organizaciones de la sociedad civil). Implementamos herramientas para la medición de percepción y el manejo de quejas y reclamos, entre otros.</div><div><br></div>","36.jpg","5");
INSERT INTO service VALUES("37","Análisis de riesgos socio-ambientales, priorización y estrategias de prevención y manejo","","<p class=\"MsoNormal\" style=\"font-style: normal; font-variant: normal; font-weight: normal; line-height: normal; text-align: justify;\"><span lang=\"ES\"><font size=\"2\" face=\"Arial\">Proporcionamos a las\nempresas un detallado análisis de los riesgos socio-ambientales, así como estrategias de prevención y manejo. Igualmente, analizamos el entorno regulatorio y sus implicaciones en el negocio.&nbsp;</font></span></p><p class=\"MsoNormal\" style=\"font-style: normal; font-variant: normal; font-weight: normal; line-height: normal; text-align: justify;\"><span lang=\"ES\"><font size=\"2\" face=\"Arial\">Diseñamos y acompañamos la implementación de la estrategia general\npara la gestión socio-ambiental, a partir de la implementación de estrategias de\nrelacionamiento con stakeholders.&nbsp;</font></span></p><p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\"></span></p><p class=\"MsoNormal\"></p><p class=\"MsoNormal\"></p><p class=\"MsoNormal\"></p><ul><li style=\"text-indent: -24px;\"><font class=\"Apple-style-span\" face=\"Arial\" size=\"2\"><br></font></li></ul><p></p><p></p><p class=\"MsoNormal\"><br></p><div style=\"font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;\"><br></div><p></p><p></p>","37.jpg","6");
INSERT INTO service VALUES("38","Estándares y mejores prácticas ","","<font face=\"Arial, Verdana\" size=\"2\">Asesoramos en la adopción de estándares nacionales e internacionales y mejores prácticas en materia de relaciones con la Fuerza Pública y las empresas de Vigilancia y Seguridad Privada.</font>","38.jpg","3");
INSERT INTO service VALUES("39","Asesoría en la construcción de Política y Programas en Derechos Humanos","","Diseñamos y acompañamos la implementación de políticas, planes y programas en Derechos Humanos, en el marco de las buenas prácticas y los estándares internacionales en DDHH y DIH.&nbsp;","39.jpg","4");
INSERT INTO service VALUES("40","Identificación y manejo de riesgos empresariales en DDHH","","Identificamos riesgos empresariales en Derechos Humanos y diseñamos estrategias para su prevención y correspondiente manejo.","40.jpg","4");
INSERT INTO service VALUES("41","Generación de capacidades","","Realizamos talleres para generar capacidades en toda la cadena de valor de la organización y para promover la aplicación de buenas prácticas y estándares en Derechos Humanos.","41.jpg","4");
INSERT INTO service VALUES("42","Elaboración de protocolos corporativos","","Elaboramos protocolos corporativos y mecanismos de aseguramiento para garantizar el cumplimiento de prácticas y estándares en DDHH y DIH.&nbsp;","42.jpg","4");
